import { Box } from '@material-ui/core'
import React, {useRef, useLayoutEffect} from 'react'

import useStyles from '../style';
import * as d3 from 'd3';

const draw = (div) => {
    const config = {
        size: 40,
        grid: 10,
        padding: 2,
    }

    let s = config.size, p=config.padding, width = config.grid * (s + p), height = width;

    const snapToGrid = (x, y) => {
        let pos = {
            x: Math.max(0, Math.floor(x / (s + p))),
            y: Math.max(0, Math.floor(y / (s + p)))
        }
        if (y >= config.grid) {
            pos.y = height + s * 2
            pos.x = (width - s) / 2
        } else {
            pos.y = Math.min(config.grid-1, pos.y) * (s + p)
            pos.x = Math.min(config.grid-1, pos.x) * (s + p)
        }
        config.info(pos)
        return pos
    }

    function dragstarted() {
        d3.select(this).attr("stroke", "black");
    }

    function dragged(event) {
        let pos = snapToGrid(event.x, event.y);
        d3.select(this).raise().attr("x", pos.x).attr("y", pos.y);
    }

    function dragended() {
        d3.select(this).attr("stroke", null);
    }

    const drag = d3.drag()
          .on("start", dragstarted)
          .on("drag", dragged)
          .on("end", dragended);

    d3.select(div).selectAll('*').remove();

    const svg = d3.select(div).append("svg")
        .attr("width", width + p * 2)
        .attr("height", (height + p) * 2)
        .append("g")
        .attr("transform","translate("+p+","+p+")");

    const board = d3.range(config.grid).map((r, i) => 
        d3.range(config.grid).map((c, j) => 
            ({
                x: c * (s + p), 
                y: i * (s + p), 
                free: true 
            })));

    let row = svg.selectAll(".row")
        .data(board)
        .enter().append("g")
        .attr("class", "row");

    let column = row.selectAll(".square")
        .data((d) => d)
        .enter().append("rect")
        .attr("class","square")
        .attr("x", (d) => d.x )
        .attr("y", (d) => d.y )
        .attr("width", s )
        .attr("height", s )
        .attr("rx", s/10)
        .attr("ry", s/10)
        .style("fill", "#fff")
        .style("stroke", "#444");

    let target = svg.append("rect")
        .attr("class","target")
        .attr("x", (width - s) / 2 )
        .attr("y", height + s * 2 )
        .attr("width", s )
        .attr("height", s )
        .attr("rx", s/10)
        .attr("ry", s/10)
        .style("fill", "#ff7f00")
        .style("stroke", "#000")
        .call(drag);


}

const Board = () => {
    const classes = useStyles();
    const widgetRef = useRef(null);

    useLayoutEffect(() => {

        draw(widgetRef.current);

    });

    return <Box className={classes.board} ref={widgetRef}>

    </Box>
}

export default Board;
