import { Box } from '@material-ui/core'
import React, {useRef, useLayoutEffect} from 'react'

import useStyles from '../style';
import * as d3 from 'd3';
import { generateStones } from './Stones'

const draw = (div, config, handleGameChange) => {

    let s = config.size, p=config.padding, width = config.grid * (s + p), height = width;
    let col = d3.scaleOrdinal().range(config.colors);

    const board = d3.range(config.grid).map((r) => 
        d3.range(config.grid).map((c) => 
            ({
                x: c * (s + p), 
                y: r * (s + p), 
                free: true 
            })));

    let stones = generateStones(config.stones), 
        drawnStones = [], 
        currentStoneNrs = [], 
        stonesLayed = 0, 
        movingStone = 0, 
        round=1;

    const snapToGrid = (x, y) => {
        let pos = {
            c: Math.max(0, Math.floor(x / (s + p))),
            r: Math.max(0, Math.floor(y / (s + p)))
        }
        if (pos.r >= config.grid) {
            pos.y = height + s * 2
            pos.x = (width - s) / 2
            pos.c = pos.r = null;
        } else {
            pos.y = Math.min(config.grid-1, pos.r) * (s + p)
            pos.x = Math.min(config.grid-1, pos.c) * (s + p)
        }
        return pos
    }

    const checkPosition = (pos) => {
        const stn = drawnStones[movingStone]
        let flag = true;
        stn.forEach(t => {
            if (pos.c===null || pos.r===null){
                flag = false
            } else if (t[0]+pos.c >= config.grid || t[0]+pos.c < 0 || t[1]+pos.r >= config.grid || t[1]+pos.r < 0){
                flag = false
            } else if (board[t[1]+pos.r][t[0]+pos.c].free===false){
                flag = false;
            }
        })
        return flag;
    }

    const moveStone = (x, y) => {
        const stn = drawnStones[movingStone];
        stn.forEach(t => t.attr("transform","translate("+(x)+","+(y)+")"));
    }

    const setPosition = (pos, val) => {
        const stn = drawnStones[movingStone]
        stn.forEach(t => {
            board[t[1]+pos.r][t[0]+pos.c].free = val;
        });
        board.map(a => console.info(a.map(b => b.free ? 1 : 0).join(",")))
    }

    function dragstarted(event) {
        movingStone = +d3.select(this).attr("stn");
        if (currentStoneNrs.indexOf(movingStone) < 0){ //(movingStone !== currentStoneNr){
            movingStone = null; 
        }
    }

    function dragged(event) {
        if (movingStone===null){return;}
        let pos = snapToGrid(event.x, event.y);
        moveStone(pos.x, pos.y)
        drawnStones[movingStone].forEach(m => {
            pos.x = Math.min(pos.x, m.attr("x"))
            pos.y = Math.min(pos.y, m.attr("y"))
        });
        let check  = checkPosition(pos);
        drawnStones[movingStone].forEach(m => {
            m.attr("opacity", check===false ? 0.5 : 1)
        });
    }

    function dragended(event) {
        if (movingStone===null){return;}
        let pos = snapToGrid(event.x, event.y);
        let check  = checkPosition(pos);

        if (check===true){
            setPosition(pos, false)
            currentStoneNrs.splice(currentStoneNrs.indexOf(movingStone), 1);
            // currentStoneNr++;
            drawStone();
        } else {
            pos = snapToGrid(width, height);
            moveStone(pos.x, pos.y);
            drawnStones[movingStone].forEach(m => {
                m.attr("opacity", 1)
            });
        }
    }

    const drag = d3.drag()
          .on("start", dragstarted)
          .on("drag", dragged)
          .on("end", dragended);

    d3.select(div).selectAll('*').remove();

    const svg = d3.select(div).append("svg")
        .attr("width", width + p * 2)
        .attr("height", (height + p) * 2)
        .append("g")
        .attr("transform","translate("+p+","+p+")");

    let row = svg.selectAll(".row")
        .data(board)
        .enter().append("g")
        .attr("class", "row");

    row.selectAll(".square")
        .data((d) => d)
        .enter().append("rect")
        .attr("class","square")
        .attr("x", (d) => d.x )
        .attr("y", (d) => d.y )
        .attr("width", s )
        .attr("height", s )
        .attr("rx", s/10)
        .attr("ry", s/10)
        .style("fill", "#fff")
        .style("stroke", "#444");

    const drawStonePieces  = (stn, color, index) => {
        let pieces = []
        stn.forEach((cell, i) => {
            let piece = svg.append("rect")
                .attr("class","stn")
                .attr("stn", index)
                .attr("x", cell[0] * (s + p) )
                .attr("y", cell[1] * (s + p))
                .attr("width", s )
                .attr("height", s )
                .attr("rx", s/10)
                .attr("ry", s/10)
                .style("fill", col(color))
                .style("stroke", "#000")
                .attr("transform","translate("+((width - s) / 2)+","+(height + s * 2)+")")
                .call(drag);
                pieces.push(piece)
        })
        return pieces;
    }
    const drawStone = () => {
        if (currentStoneNrs.length>0){return}
        if (stones.length < 3){
            stones.push(...generateStones(config.stones));
            round++;
            handleGameChange('round', round)
        }
        for (let i = 0, I = 3; i < I; i++){
            const stn = stones.shift();
            let pieces = drawStonePieces(stn.stone, stn.type, stonesLayed);
            drawnStones.push(pieces);
            currentStoneNrs.push(stonesLayed);
            handleGameChange('step', stonesLayed);
            stonesLayed++;
        }
        // const stn = stones[currentStoneNr].stone;
        // const color = stones[currentStoneNr].type;
        // if (currentStoneNr === config.stones){
        //     stones = generateStones(config.stones);
        //     currentStoneNr = 0;
        //     round++;
        //     handleGameChange('round', round+1)
        // }

    }

    drawStone();

}

const Board = ({config, game, handleChange}) => {
    const classes = useStyles();
    const widgetRef = useRef(null);

    const handleGameChange = (key, value) => {
        handleChange(key, value);
    }

    useLayoutEffect(() => {
        if (!game || game.step > 1 || game.round > 1){ return; }
        draw(widgetRef.current, config, handleGameChange);
    });

    return <Box className={classes.board} ref={widgetRef}></Box>

}

export default Board;
