import { Box } from '@material-ui/core'
import React, {useRef, useLayoutEffect} from 'react'

import useStyles from '../style';
import * as d3 from 'd3';

const draw = (div) => {
    const config = {
        size: 20,
        grid: 10,
        padding: 3,
    }

    let width = config.grid * config.size, height = width;
    const svg = d3.select(div).append("svg")
        .attr("width", width + config.padding * 2)
        .attr("height", height + config.padding * 2)
        .append("g")
        .attr("transform","translate("+config.padding+","+config.padding+")");

    const board = d3.range(config.grid).map(r => d3.range(config.grid));

    debugger;



}

const Board = () => {
    const classes = useStyles();
    const widgetRef = useRef(null);

    useLayoutEffect(() => {

        draw(widgetRef.current);

    });

    return <Box className={classes.board} ref={widgetRef}>
        Board
    </Box>
}

export default Board;
