import './App.css';
import {Box} from '@material-ui/core'
import React, { useEffect } from 'react';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { TextField } from '@material-ui/core';

import useStyles from './style';
import Board from './widgets/Board'
import { useState , useCallback} from 'react';
import {Refresh} from './widgets/utils';

function App() {
  const classes = useStyles();
  const [user, setUser] = useState('')
  const [points, setPoints] = useState(localStorage.getItem('points') || 0);

  useEffect(()=>{
    localStorage.setItem("points", points);
  }, [points]);

  const handleChange = useCallback((key, value) => {
    if (value===0 || key!=='points'){ return; }
    setPoints(p => +p+value);
  },[]);

  const config = {
    size: 40,
    grid: 10,
    padding: 2,
    stones: 3,
    colors: ["#8dd3c7","#ffffb3","#bebada","#fb8072","#80b1d3","#fdb462","#b3de69","#fccde5","#d9d9d9","#bc80bd","#ccebc5","#ffed6f"],
    colors1: ["#66c2a5","#fc8d62","#8da0cb","#e78ac3","#a6d854","#ffd92f","#e5c494","#b3b3b3"]
  }

  return (
    <Box className={classes.root}>
      <header className={classes.header}>
        <h2 className={classes.h2}>
          My 1010
        </h2>
        <Autocomplete
          value={user}
          onChange={(event, newValue) => {
            if (typeof newValue === 'string') {
              setUser(newValue);
            } else if (newValue && newValue.inputValue) {
              // Create a new value from the user input
              setUser(newValue.inputValue);
            } else {
              setUser(newValue);
            }
          }}
          selectOnFocus
          clearOnBlur
          handleHomeEndKeys
          options={['Peter']}
          getOptionLabel={(option) => {
            // Value selected with enter, right from the input
            if (typeof option === 'string') {
              return option;
            }
            // Add "xxx" option created dynamically
            if (option.inputValue) {
              return option.inputValue;
            }
            // Regular option
            return option;
          }}
          renderOption={(option) => option}
          style={{ width: 200 }}
          freeSolo
          renderInput={(params) => (
            <TextField {...params} />
          )}
        />
        <h3 className={classes.h3}>Points: {points}</h3>
        <Box className={classes.refresh}>
          <Refresh/>
      </Box>
      </header>
      <Board config={config} points={points} handleChange={handleChange}/>
    </Box>
  );
}

export default App;
