import './App.css';
import {Box, Button} from '@material-ui/core'
import React, { useEffect } from 'react';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { TextField } from '@material-ui/core';

import useStyles from './style';
import Board from './widgets/Board'
import { useState , useCallback} from 'react';
import {Refresh} from './widgets/utils';

function App() {
  const classes = useStyles();
  const [user, setUser] = useState('')
  const [userOptions, setUserOptions]  = useState([''])
  const [points, setPoints] = useState(0);
  const [maxPoints, setMaxPoints] = useState(0)

  useEffect(()=>{
    if (!user){ return; }
    let my1010UserPoints = JSON.parse(localStorage.getItem("my1010") || "{}"); 
    if (!my1010UserPoints[user]){
      my1010UserPoints[user] = {'points':0, 'max':0}
    }
    my1010UserPoints[user]["points"] = points;
    if (my1010UserPoints[user]["max"] <= points){
      my1010UserPoints[user]["max"] = points
    }
    localStorage.setItem("my1010",JSON.stringify(my1010UserPoints));
  }, [user, points]);

  useEffect(()=>{
    if (!user){ return; }
    let my1010UserPoints = JSON.parse(localStorage.getItem("my1010") || "{}"); 
    if (!my1010UserPoints[user]){
      my1010UserPoints[user] = {'points':0, 'max':0}
    }
    setPoints(my1010UserPoints[user]['points'])
    setMaxPoints(my1010UserPoints[user]['max'])
    setUserOptions(Object.keys(my1010UserPoints))
  }, [user]);

  const handleChange = useCallback((key, value) => {
    if (value===0 || key!=='points'){ return; }
    setPoints(p => +p+value);
  },[]);

  const onHandleRefresh = () => {
    setPoints(0)
  }

  const config = {
    size: 40,
    grid: 10,
    padding: 2,
    stones: 3,
    colors: ["#8dd3c7","#ffffb3","#bebada","#fb8072","#80b1d3","#fdb462","#b3de69","#fccde5","#d9d9d9","#bc80bd","#ccebc5","#ffed6f"],
    colors1: ["#66c2a5","#fc8d62","#8da0cb","#e78ac3","#a6d854","#ffd92f","#e5c494","#b3b3b3"]
  }

  return (
    <Box className={classes.root}>
      <header className={classes.header}>
        <h2 className={classes.h2}>
          My 1010
        </h2>
        <Autocomplete
          value={user}
          onChange={(event, newValue) => {
            if (newValue && newValue.inputValue) {
              // Create a new value from the user input
              setUser(newValue.inputValue);
            } else {
              setUser(newValue);
            }
          }}
          selectOnFocus
          clearOnBlur
          handleHomeEndKeys
          options={userOptions}
          getOptionLabel={(option) => {
            if (option.inputValue) {
              return option.inputValue;
            }
            return option;
          }}
          renderOption={(option) => option}
          style={{ width: 120 }}
          freeSolo
          renderInput={(params) => (
            <TextField {...params} placeholder='user'/>
          )}
        />
        <h3 className={classes.h3}>: {points} ({maxPoints})</h3>
        <Box className={classes.refresh}>
          <Button onClick={onHandleRefresh}><Refresh/></Button>
      </Box>
      </header>
      <Board config={config} points={points} handleChange={handleChange}/>
    </Box>
  );
}

export default App;
