import { Box } from '@material-ui/core'
import React, {useRef, useLayoutEffect} from 'react'

import useStyles from '../style';
import * as d3 from 'd3';
import Items from './Items'

const generateTargets = (N=20, E=Items.length) => {
    let targets = d3.range(N).map(n => {
        return Math.floor(Math.random() * E)
    })
    return targets
}

const draw = (div) => {
    const config = {
        size: 40,
        grid: 10,
        padding: 2,
        stones: 43
    }

    let s = config.size, p=config.padding, width = config.grid * (s + p), height = width;

    const snapToGrid = (x, y) => {
        let pos = {
            c: Math.max(0, Math.floor(x / (s + p))),
            r: Math.max(0, Math.floor(y / (s + p)))
        }
        if (pos.r >= config.grid) {
            pos.y = height + s * 2
            pos.x = (width - s) / 2
            pos.c = pos.r = null;
        } else {
            pos.y = Math.min(config.grid-1, pos.r) * (s + p)
            pos.x = Math.min(config.grid-1, pos.c) * (s + p)
        }
        return pos
    }

    const checkPosition = (pos) => {
        // there is a BUG = flag is always true ;-()
        const target = targetGrid[movingTarget]
        let flag = true;
        target.forEach(t => {
            if (pos.c===null || pos.r===null){
                flag = false
            } else if (t[0]+pos.c >= config.grid || t[0]+pos.c < 0 || t[1]+pos.r >= config.grid || t[1]+pos.r < 0){
                flag = false
            } else if (board[t[1]+pos.r][t[0]+pos.c].free===false){
                flag = false;
            }
        })
        return flag;
    }

    const getPosition = (event) => {
        let pos = {x: event.x, y: event.y}
        drawnTargets[movingTarget].forEach(m => {
            pos.x = Math.min(pos.x, m.attr("x"))
            pos.y = Math.min(pos.y, m.attr("y"))
        });
        pos = snapToGrid(pos.x, pos.y)
        return posl
    }
    const setPosition = (pos, val) => {
        const target = targetGrid[movingTarget]
        target.forEach(t => {
            board[t[1]+pos.r][t[0]+pos.c].free = val;
        });
        board.map(a => console.info(a.map(b => b.free ? 1 : 0).join(",")))
    }

    function dragstarted(event) {
        movingTarget = +d3.select(this).attr("target");
        if (movingTarget < currentTargetNr){
            svg.selectAll('[target="'+currentTargetNr+'"]').remove();
            drawnTargets.splice(currentTargetNr, 1)
            let pos = getPosition(event)
            setPosition(pos, true)
            currentTargetNr--;
        }
    }

    function dragged(event) {
        moveTarget(event.x, event.y);
        let pos = {x:event.x, y:event.y};
        drawnTargets[movingTarget].forEach(m => {
            pos.x = Math.min(pos.x, m.attr("x"))
            pos.y = Math.min(pos.y, m.attr("y"))
        });
        pos = snapToGrid(pos.x, pos.y)
        let check  = checkPosition(pos);
        drawnTargets[movingTarget].forEach(m => {
            m.attr("opacity", check===false ? 0.5 : 1)
        });
    }

    function dragended(event) {
        let pos = snapToGrid(event.x, event.y);
        let check  = checkPosition(pos);

        if (check===true){
            setPosition(pos, false)
            currentTargetNr++;
            drawTarget();
        } else {
            pos = snapToGrid(width, height);
            moveTarget(pos.x, pos.y);
            drawnTargets[movingTarget].forEach(m => {
                m.attr("opacity", 1)
            });
        }
    }

    const drag = d3.drag()
          .on("start", dragstarted)
          .on("drag", dragged)
          .on("end", dragended);

    d3.select(div).selectAll('*').remove();

    const svg = d3.select(div).append("svg")
        .attr("width", width + p * 2)
        .attr("height", (height + p) * 2)
        .append("g")
        .attr("transform","translate("+p+","+p+")");

    const board = d3.range(config.grid).map((r) => 
        d3.range(config.grid).map((c) => 
            ({
                x: c * (s + p), 
                y: r * (s + p), 
                free: true 
            })));

    let row = svg.selectAll(".row")
        .data(board)
        .enter().append("g")
        .attr("class", "row");

    row.selectAll(".square")
        .data((d) => d)
        .enter().append("rect")
        .attr("class","square")
        .attr("x", (d) => d.x )
        .attr("y", (d) => d.y )
        .attr("width", s )
        .attr("height", s )
        .attr("rx", s/10)
        .attr("ry", s/10)
        .style("fill", "#fff")
        .style("stroke", "#444");

    let targetGrid = generateTargets(config.stones, Items.length).map(t => Items[t])
    let drawnTargets = [];
    let currentTargetNr = 0, movingTarget = 0;

    const drawTarget = (x = (width - s) / 2, y = height + s * 2) => {
        const target = targetGrid[currentTargetNr];
        let items = []
        target.forEach((cell, i) => {
            let item = svg.append("rect")
                .attr("class","target")
                .attr("target", currentTargetNr)
                .attr("x", cell[0] * (s + p) )
                .attr("y", cell[1] * (s + p))
                .attr("width", s )
                .attr("height", s )
                .attr("rx", s/10)
                .attr("ry", s/10)
                .style("fill", "#ff7f00")
                .style("stroke", "#000")
                .attr("transform","translate("+(x)+","+(y)+")")
                .call(drag);
            items.push(item)
        })
        drawnTargets.push(items)
    }

    const moveTarget = (x, y) => {
        const target = drawnTargets[movingTarget];
        target.forEach(t => t.attr("transform","translate("+(x)+","+(y)+")"));
    }

    drawTarget();

}

const Board = () => {
    const classes = useStyles();
    const widgetRef = useRef(null);

    useLayoutEffect(() => {

        draw(widgetRef.current);

    });

    return <Box className={classes.board} ref={widgetRef}>

    </Box>
}

export default Board;
