import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    height: '100%',
  },
  header: {
    display:'flex',
    flexDirection: 'column', 
    justifyContent: 'center', 
    alignItems: "center",
    textAlign:'center',
    fontFamily: 'fantasy',
    color: '#444',
    padding: '0.6em',
    backgroundColor: '#8dd3c7'
  },
  h2:{
    margin:'0',
    fontSize:'1.7em',
    color:'#444'
  },
  board:{
    display:'flex',
    width:'100%',
    flexDirection: 'column',
    justifyContent: 'center', 
    alignItems: "center",
    textAlign:'center',
    padding: '0.2em',
    backgroundColor: '#f2f2f2'
  }

}));

export default useStyles;
