import './App.css';
import {Box} from '@material-ui/core'

import useStyles from './style';
import Board from './widgets/Board'
import { useState } from 'react';

function App() {
  const classes = useStyles();
  const [game, setGame] = useState({step: 1, round: 1});

  const handleChange = (key, value) => {
    if (game[key]===value){ return; }
    let newGame = {...game};
    newGame[key] = value
    setGame(newGame);
  }

  const config = {
    size: 40,
    grid: 10,
    padding: 2,
    stones: 43,
    colors: ["#8dd3c7","#ffffb3","#bebada","#fb8072","#80b1d3","#fdb462","#b3de69","#fccde5","#d9d9d9","#bc80bd","#ccebc5","#ffed6f"],
    colors1: ["#66c2a5","#fc8d62","#8da0cb","#e78ac3","#a6d854","#ffd92f","#e5c494","#b3b3b3"]
  }

  return (
    <Box className={classes.root}>
      <header className={classes.header}>
        <h2 className={classes.h2}>
          My 1010: Game: {game.round}, Step: {game.step} ({config.stones})
        </h2>
      </header>

      <Board config={config} handleChange={handleChange}/>
    </Box>
  );
}

export default App;
